import axios from 'axios';

const BASE_URL = 'http://localhost:8080'; // Make sure your Java backend runs here

export const getTasks = () => axios.get(`${BASE_URL}/tasks`);

export const getTaskByName = (name: string) =>
  axios.get(`${BASE_URL}/tasks/search?name=${name}`);

export const createTask = (task: any) =>
  axios.put(`${BASE_URL}/tasks`, task);

export const deleteTask = (id: string) =>
  axios.delete(`${BASE_URL}/tasks/${id}`);

export const runTask = (id: string) =>
  axios.put(`${BASE_URL}/tasks/${id}/run`);
