import { Button, Input, List, Typography } from 'antd';
import { useEffect, useState } from 'react';
import { deleteTask, getTasks, getTaskByName, runTask } from '../api/TaskService';

// âœ… Define Task type
interface Task {
  id: string;
  name: string;
  owner: string;
  command: string;
  taskExecutions?: {
    startTime: string;
    endTime: string;
    output: string;
  }[];
}

const TaskList = () => {
  const [tasks, setTasks] = useState<Task[]>([]);
  const [search, setSearch] = useState('');

  // âœ… Fetch all tasks
  const fetchTasks = async () => {
    try {
      const res = await getTasks();
      setTasks(res.data);
    } catch (err) {
      console.error('Error fetching tasks', err);
    }
  };

  // âœ… On load
  useEffect(() => {
    fetchTasks();
  }, []);

  // âœ… Handle search by name
  const onSearch = async () => {
    if (!search) {
      fetchTasks();
      return;
    }

    try {
      const res = await getTaskByName(search);
      setTasks(res.data);
    } catch (err) {
      console.error('Search failed', err);
      setTasks([]);
    }
  };

  // âœ… Handle delete
  const handleDelete = async (id: string) => {
    await deleteTask(id);
    fetchTasks();
  };

  // âœ… Handle run
  const handleRun = async (id: string) => {
    await runTask(id);
    fetchTasks();
  };

  return (
    <>
      {/* âœ… Search bar */}
      <Input.Search
        placeholder="Search by name"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        onSearch={onSearch}
        enterButton
        style={{ marginBottom: 20 }}
      />

      {/* âœ… Task List */}
      <List
        bordered
        dataSource={tasks}
        renderItem={(task: Task) => (
          <List.Item
            actions={[
              <Button onClick={() => handleRun(task.id)}>Run</Button>,
              <Button danger onClick={() => handleDelete(task.id)}>Delete</Button>
            ]}
          >
            <List.Item.Meta
              title={`${task.name} (Owner: ${task.owner})`}
              description={
                <>
                  <Typography.Text code>{task.command}</Typography.Text>
                  <br />
                  {task.taskExecutions?.map((exe, i) => (
                    <div key={i}>
                      ðŸ•’ {exe.startTime} â†’ {exe.endTime}
                      <br />
                      Output: <pre>{exe.output}</pre>
                    </div>
                  ))}
                </>
              }
            />
          </List.Item>
        )}
      />
    </>
  );
};

export default TaskList;
